{
    "description": "Notify me of all product price changes",
    "subject": {
        "entities": [
            {
                "idPattern": "urn:ngsi-ld:Product:010",
                "type": "Product"
            }
        ],
        "condition": {
            "attrs": [
                "price"
            ]
        }
    },
    "notification": {
        "http": {
            "url": "http://context-provider:5000/priceChange"
        }
    }
}

{
    "description": "Notify me of all product price changes when less than 50",
    "subject": {
        "entities": [
            {
                "idPattern": "urn:ngsi-ld:Product:010",
                "type": "Product"
            }
        ],
        "condition": {
            "attrs": [
                "price"
            ],
            "expression": {
                "q": "price<50;"
            }
        }
    },
    "notification": {
        "http": {
            "url": "http://context-provider:5000/priceChange"
        },
        "attrsFormat": "keyValues"
    }
}
